import { Component } from '@angular/core';
import { ApiService } from './services/api.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  username: string = '';
  user: any;
  location: any;
  repositories: any[] = [];
  loading: boolean = false;

  constructor(private apiService: ApiService) {}

  searchUser(): void {
  this.loading = true;

  // Fetch user details first
  this.apiService.getRepoDetails(this.username, this.username).subscribe(
    (user: any) => {
      this.user = user;
      console.log('User Object:', this.user);

      // Now, fetch all repositories without pagination
      this.apiService.getUserRepositories(this.username, 1, Number.MAX_SAFE_INTEGER).subscribe(
        (repos) => {
          this.repositories = repos;
          this.loading = false;
        },
        (error) => {
          console.error(error);
          this.loading = false;
        }
      );
    },
    (error: any) => {
      console.error(error);
      this.loading = false;
    }
  );
}


}
