import { Component } from '@angular/core';
import { ApiService } from './services/api.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  username: string = '';
  user: any;
  location:any;
  repositories: any[] = [];
  loading: boolean = false;
  currentPage: number = 1;
  itemsPerPage: number = 6;
  pages: number[] = [];

  constructor(private apiService: ApiService) {}

  searchUser(): void {
    this.loading = true;
    this.apiService.getUserRepositories(this.username, this.currentPage, this.itemsPerPage).subscribe(
      (repos) => {
        this.repositories = repos;
        this.loading = false;
        this.updatePages(repos.length); // Update the page numbers based on the total repositories
      },
      (error) => {
        console.error(error);
        this.loading = false;
      }
    );

    this.apiService.getRepoDetails(this.username, this.username).subscribe(
      (user) => {
        this.user = user;
        console.log('User Object:', this.user);

      },
      (error) => {
        console.error(error);
      }
    );
  }

  private updatePages(totalRepos: number): void {
    const totalPages = Math.ceil(totalRepos / this.itemsPerPage);
    this.pages = Array.from({ length: totalPages }, (_, index) => index + 1);
  }

  getDisplayedRepos(): any[] {
    const startIndex = (this.currentPage - 1) * this.itemsPerPage;
    const endIndex = startIndex + this.itemsPerPage;
    return this.repositories.slice(startIndex, endIndex);
  }

  onNextPage(): void {
    if (this.currentPage < this.pages.length) {
      this.currentPage++;
    }
  }
  onPageChange(pageNumber: number): void {
    this.currentPage = pageNumber;
    this.searchUser();
  }

  onPreviousPage(): void {
    if (this.currentPage > 1) {
      this.currentPage--;
    }
  }
}
<div class="container">
  <div class="input-container">
    <input id="email-address" name="email" type="email" autocomplete="email" required 
      class="min-w-0 flex-auto rounded-md border-red-500 bg-white/5 px-3.5 py-2 text-black shadow-sm ring-1 ring-inset ring-white/10 focus:ring-2 focus:ring-inset focus:ring-indigo-500 sm:text-sm sm:leading-6" 
      [(ngModel)]="username" placeholder="Enter GitHub username">
      
    <!-- Updated button with the specified styling and padding -->
    <button type="submit" class="flex-none rounded-md bg-gradient-to-r from-purple-800 to-black px-3.5 py-2.5 text-sm font-semibold text-white shadow-sm hover:from-purple-700 hover:to-black focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-indigo-500"
      (click)="searchUser()">Search
    </button>
  </div>
  

  
  <div *ngIf="user" class="user-profile">
    <img [src]="user.owner.avatar_url" alt="User Profile" class="user-avatar">
    <div class="user-info">
      <h2>{{ user.name }}</h2>
      <p>bio:{{ user.owner.bio_url }}</p>
      <p>location: {{ user.owner.location_url }}</p>
      <p>followers: {{ user.followers }}</p>
      <p>following: {{ user.following }}</p>
      <p>public_repos: {{ user.public_repos }}</p>
    </div>
  </div>
  
  
  <div *ngIf="loading" class="loader"></div>
  
  <div *ngIf="!loading" class="grid">
    <div *ngFor="let repo of repositories" class="card">
      <h3 class="card-title">{{ repo.name }}</h3>
      <p *ngIf="repo.description" class="card-description">{{ repo.description }}</p>
      <p *ngIf="repo.language" class="inline-block bg-blue-500 rounded-full px-3 py-1 text-sm font-semibold text-white-200 mr-2 mb-2">{{ repo.language }}</p>
    </div>
  </div>
  
  <!-- Pagination -->
<div class="flex items-center justify-between border-t border-gray-200 bg-white px-4 py-3 sm:px-6">
  <nav class="isolate inline-flex -space-x-px rounded-md shadow-sm" aria-label="Pagination">
    <!-- Previous button -->
    <a href="#" class="relative inline-flex items-center rounded-l-md px-2 py-2 text-gray-400 ring-1 ring-inset ring-gray-300 hover:bg-gray-50 focus:z-20 focus:outline-offset-0">
      <span class="sr-only">Previous</span>
      <svg class="h-5 w-5" viewBox="0 0 20 20" fill="currentColor" aria-hidden="true">
        <path fill-rule="evenodd" d="M12.79 5.23a.75.75 0 01-.02 1.06L8.832 10l3.938 3.71a.75.75 0 11-1.04 1.08l-4.5-4.25a.75.75 0 010-1.08l4.5-4.25a.75.75 0 011.06.02z" clip-rule="evenodd" />
      </svg>
    </a>
    <!-- Dynamic page numbers -->
    <ng-container *ngFor="let page of pages">
      <a href="#" (click)="onPageChange(page)" [class.active]="currentPage === page" class="relative inline-flex items-center px-4 py-2 text-sm font-semibold text-gray-900 ring-1 ring-inset ring-gray-300 hover:bg-gray-50 focus:z-20 focus:outline-offset-0">
        {{ page }}
      </a>
    </ng-container>
    <!-- Next button -->
    <a href="#" class="relative inline-flex items-center rounded-r-md px-2 py-2 text-gray-400 ring-1 ring-inset ring-gray-300 hover:bg-gray-50 focus:z-20 focus:outline-offset-0">
      <span class="sr-only">Next</span>
      <svg class="h-5 w-5" viewBox="0 0 20 20" fill="currentColor" aria-hidden="true">
        <path fill-rule="evenodd" d="M7.21 14.77a.75.75 0 01.02-1.06L11.168 10 7.23 6.29a.75.75 0 111.04-1.08l4.5 4.25a.75.75 0 010 1.08l-4.5 4.25a.75.75 0 01-1.06-.02z" clip-rule="evenodd" />
      </svg>
    </a>
  </nav>
</div>

</div>
