import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';  // Import the map operator

@Injectable({
  providedIn: 'root'
})
export class ApiService {
  private apiUrl = 'https://api.github.com';

  constructor(private http: HttpClient) { }

  getUserRepositories(username: string, page: number, perPage: number): Observable<any[]> {
    const url = `${this.apiUrl}/users/${username}/repos`;
    const params = {
      page: page.toString(),
      per_page: perPage.toString()
    };
    return this.http.get<any[]>(url, { params });
  }

  getRepoDetails(username: string, repoName: string): Observable<any> {
    const url = `${this.apiUrl}/repos/${username}/${repoName}`;
    return this.http.get<any>(url);
  }

  getUserProfile(username: string): Observable<any> {
    const url = `${this.apiUrl}/users/${username}`;
    
    // Use the `?additional_details=true` query parameter to get additional details
    return this.http.get<any>(`${url}?additional_details=true`).pipe(
      // Transform the response to extract the required information
      map((user) => ({
        ...user,
        followers: user.followers_url, // You can replace this with the actual number if the API provides it
        following: user.following_url, // You can replace this with the actual number if the API provides it
        public_repos: user.public_repos_url // You can replace this with the actual number if the API provides it
      }))
    );
  }
}
