import { Component } from '@angular/core';
import { ApiService } from './services/api.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  username: string = '';
  repositories: any[] = [];
  loading: boolean = false;

  constructor(private apiService: ApiService) {}

  searchUser(): void {
    this.loading = true;
    this.apiService.getUserRepositories(this.username, 1, 10).subscribe(
      (repos) => {
        this.repositories = repos;
        this.loading = false;
      },
      (error) => {
        console.error(error);
        this.loading = false;
      }
    );
  }
}
<div class="container">
  <div class="profile-container">
    <div class="profile">
      <img [src]="user.avatar_url" alt="Profile Photo" class="profile-photo">
      <div class="profile-details">
        <h2 class="profile-name">{{ user.name }}</h2>
        <p class="profile-bio">{{ user.bio }}</p>
        <p class="profile-location">{{ user.location }}</p>
        <div class="social-links">
          <a *ngIf="user.blog" [href]="user.blog" target="_blank">Website</a>
          <a *ngIf="user.twitter_username" [href]="'https://twitter.com/' + user.twitter_username" target="_blank">Twitter</a>
          <a *ngIf="user.html_url" [href]="user.html_url" target="_blank">GitHub</a>
        </div>
      </div>
    </div>
  </div>
  
  <div *ngIf="loading" class="loader"></div>
  
  <div *ngIf="!loading" class="grid">
    <div *ngFor="let repo of repositories" class="card">
      <h3 class="card-title">{{ repo.name }}</h3>
      <p *ngIf="repo.description" class="card-description">{{ repo.description }}</p>
      <p *ngIf="repo.language" class="card-language">{{ repo.language }}</p>
    </div>
  </div>
</div>
