import { Component } from '@angular/core';
import { ApiService } from './services/api.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  username: string = '';
  user: any;
  repositories: any[] = [];
  loading: boolean = false;
  currentPage: number = 1;
  itemsPerPage: number = 6;
  totalPages: number = 0;
  pages: number[] = [];

  constructor(private apiService: ApiService) {}

  searchUser(): void {
    this.loading = true;

    this.apiService.getUserRepositories(this.username, this.currentPage, this.itemsPerPage).subscribe(
      (repos) => {
        this.repositories = repos;
        this.loading = false;
        this.updatePages(repos.length); // Update the page numbers based on the total repositories
      },
      (error) => {
        console.error(error);
        this.loading = false;
      }
    );

    this.apiService.getRepoDetails(this.username).subscribe(
      (user) => {
        this.user = user;
      },
      (error) => {
        console.error(error);
      }
    );
    
  }
  

  updatePages(totalRepositories: number): void {
    this.totalPages = Math.ceil(totalRepositories / this.itemsPerPage);
    this.pages = Array.from({ length: this.totalPages }, (_, i) => i + 1);
  }

  changePage(page: number): void {
    this.currentPage = page;
    this.searchUser(); // Reload repositories for the selected page
  }

  getPages(totalPages: number): number[] {
    return Array.from({ length: totalPages }, (_, i) => i + 1);
  }

  


}
<div class="container">
  <div class="input-container">
    <input [(ngModel)]="username" placeholder="Enter GitHub username">
    <button (click)="searchUser()">Search</button>
  </div>
  
  <div *ngIf="user" class="user-profile">
    <img [src]="user.avatar_url" alt="User Profile" class="user-avatar">
    <div class="user-info">
      <h2>{{ user.name }}</h2>
      <p>{{ user.bio }}</p>
      <p>Location: {{ user.location }}</p>
      <p>Followers: {{ user.followers }}</p>
      <p>Following: {{ user.following }}</p>
      <p>Public Repositories: {{ user.public_repos }}</p>
    </div>
  </div>
  
  <div *ngIf="loading" class="loader"></div>
  
  <div *ngIf="!loading" class="grid">
    <div *ngFor="let repo of repositories" class="card">
      <h3 class="card-title">{{ repo.name }}</h3>
      <p *ngIf="repo.description" class="card-description">{{ repo.description }}</p>
      <p *ngIf="repo.language" class="card-language">{{ repo.language }}</p>
    </div>
  </div>
  
  <!-- Pagination -->
  <div *ngIf="totalPages > 1" class="pagination">
    <button *ngFor="let page of getPages(totalPages)" (click)="changePage(page)">
      {{ page }}
    </button>
  </div>
</div>
