// app.component.ts

import { Component } from '@angular/core';
import { ApiService } from './services/api.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  username: string = '';
  user: any;
  location: any;
  repositories: any[] = [];
  loading: boolean = false;

  // Pagination properties
  currentPage: number = 1;
  itemsPerPage: number = 6; // You can adjust this value based on your preference
  totalItems: number = 0;
  Math: any;

  constructor(private apiService: ApiService) {}

  searchUser(): void {
    this.loading = true;

    // Fetch user details first
    this.apiService.getRepoDetails(this.username, this.username).subscribe(
      (user: any) => {
        this.user = user;
        console.log('User Object:', this.user);

        // Now, fetch all repositories with pagination
        this.apiService.getUserRepositories(this.username, this.currentPage, this.itemsPerPage).subscribe(
          (repos) => {
            this.repositories = repos;
            this.loading = false;
          },
          (error) => {
            console.error(error);
            this.loading = false;
          }
        );
      },
      (error: any) => {
        console.error(error);
        this.loading = false;
      }
    );
  }

  // Function to handle page change
  onPageChange(page: number): void {
    this.currentPage = page;
    this.searchUser();
  }
}
<div class="container">
  <div class="input-container">
    <input id="email-address" name="email" type="email" autocomplete="email" required 
      class="min-w-0 flex-auto rounded-md border-red-500 bg-white/5 px-3.5 py-2 text-black shadow-sm ring-1 ring-inset ring-white/10 focus:ring-2 focus:ring-inset focus:ring-indigo-500 sm:text-sm sm:leading-6" 
      [(ngModel)]="username" placeholder="Enter GitHub username">
      
    <!-- Updated button with the specified styling and padding -->
    <button type="submit" class="flex-none rounded-md bg-gradient-to-r from-purple-800 to-black px-3.5 py-2.5 text-sm font-semibold text-white shadow-sm hover:from-purple-700 hover:to-black focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-indigo-500"
      (click)="searchUser()">Search
    </button>
  </div>

  <div *ngIf="user" class="user-profile">
    <img [src]="user.owner.avatar_url" alt="User Profile" class="user-avatar">
    <div class="user-info">
      <h2>{{ user.name }}</h2>
      <p>bio:{{ user.owner.bio_url }}</p>
      <p>location: {{ user.owner.location_url }}</p>
      <p>followers: {{ user.followers }}</p>
      <p>following: {{ user.following }}</p>
      <p>public_repos: {{ user.public_repos }}</p>
    </div>
  </div>

  <div *ngIf="loading" class="loader-overlay">
    <div class="animate-pulse flex space-x-4">
      <div class="h-10 w-10 bg-slate-700 rounded"></div>
      <div class="flex-1 space-y-6 py-1">
        <div class="h-2 bg-slate-700 rounded"></div>
        <div class="space-y-3">
          <div class="grid grid-cols-3 gap-4">
            <div class="h-2 bg-slate-700 rounded col-span-2"></div>
            <div class="h-2 bg-slate-700 rounded col-span-1"></div>
          </div>
          <div class="h-2 bg-slate-700 rounded"></div>
        </div>
      </div>
    </div>
  </div>

  <!-- Display all repositories -->
  <div *ngIf="!loading" class="grid">
    <div *ngFor="let repo of repositories" class="card">
      <h3 class="card-title">{{ repo.name }}</h3>
      <p *ngIf="repo.description" class="card-description">{{ repo.description }}</p>
      <p *ngIf="repo.language" class="inline-block bg-blue-500 rounded-full px-3 py-1 text-sm font-semibold text-white-200 mr-2 mb-2">{{ repo.language }}</p>
    </div>
  </div>

  <!-- Pagination -->
  <nav aria-label="Page navigation example">
    <ul class="flex items-center -space-x-px h-8 text-sm">
      <li>
        <!-- ... (Previous button) ... -->
      </li>
      <!-- Add ngFor loop based on the number of pages -->
      <li *ngFor="let page of getPages()" [class.active]="page === currentPage">
        <a href="#" (click)="onPageChange(page)" class="flex items-center justify-center px-3 h-8 leading-tight text-gray-500 bg-white border border-gray-300 hover:bg-gray-100 hover:text-gray-700 dark:bg-gray-800 dark:border-gray-700 dark:text-gray-400 dark:hover:bg-gray-700 dark:hover:text-white">
          {{ page }}
        </a>
      </li>
      <li>
        <!-- ... (Next button) ... -->
      </li>
    </ul>
  </nav>
  
  <!-- Pagination controls with larger height -->
  <nav aria-label="Page navigation example">
    <ul class="flex items-center -space-x-px h-10 text-base">
      <li>
        <!-- ... (Previous button) ... -->
      </li>
      <!-- Add ngFor loop based on the number of pages -->
      <li *ngFor="let page of getPages()" [class.active]="page === currentPage">
        <a href="#" (click)="onPageChange(page)" class="flex items-center justify-center px-4 h-10 leading-tight text-gray-500 bg-white border border-gray-300 hover:bg-gray-100 hover:text-gray-700 dark:bg-gray-800 dark:border-gray-700 dark:text-gray-400 dark:hover:bg-gray-700 dark:hover:text-white">
          {{ page }}
        </a>
      </li>
      <li>
        <!-- ... (Next button) ... -->
      </li>
    </ul>
  </nav>

</div>
