import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable, forkJoin } from 'rxjs';
import { map } from 'rxjs/operators';

@Injectable({
  providedIn: 'root'
})
export class ApiService {
  private apiUrl = 'https://api.github.com';

  constructor(private http: HttpClient) { }

  getUserRepositories(username: string, page: number, perPage: number): Observable<any[]> {
    const url = `${this.apiUrl}/users/${username}/repos`;
    const params = {
      page: page.toString(),
      per_page: perPage.toString()
    };
    return this.http.get<any[]>(url, { params });
  }

  getRepoDetails(username: string, repoName: string): Observable<any> {
    const url = `${this.apiUrl}/repos/${username}/${repoName}`;
    return this.http.get<any>(url);
  }

  getUserProfile(username: string): Observable<any> {
    const userUrl = `${this.apiUrl}/users/${username}`;
    const followersUrl = `${this.apiUrl}/users/${username}/followers`;
    const followingUrl = `${this.apiUrl}/users/${username}/following`;
    const reposUrl = `${this.apiUrl}/users/${username}/repos`;

    return forkJoin({
      user: this.http.get<any>(userUrl),
      followers: this.http.get<any[]>(followersUrl),
      following: this.http.get<any[]>(followingUrl),
      publicRepos: this.http.get<any[]>(reposUrl)
    }).pipe(
      map((response: any) => ({
        ...response.user,
        followers: response.followers.length,
        following: response.following.length,
        public_repos: response.publicRepos.length
      }))
    );
  }
}
