import { Component } from '@angular/core';
import { ApiService } from './services/api.service';
@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  username: string = '';
  repositories: any[] = [];
  userDetails: any = null;
  loading: boolean = false;

  constructor(private apiService: ApiService) {}

  searchUser(): void {
    this.loading = true;
    this.apiService.getUserRepositories(this.username, 1, 10).subscribe(
      (repos) => {
        this.repositories = repos;
        this.loading = false;
        // Fetch user details after fetching repositories
        this.fetchUserDetails();
      },
      (error) => {
        console.error(error);
        this.loading = false;
      }
    );
  }

  fetchUserDetails(): void {
    this.apiService.getRepoDetails(this.username, this.repositories[0].name) // Assuming the first repository exists
      .subscribe(
        (userDetails) => {
          this.userDetails = userDetails.owner; // Assuming the user is the owner of the first repository
        },
        (error) => {
          console.error(error);
          // Handle error, set userDetails to null or provide default values
        }
      );
  }
}
